const prevButton = document.getElementById('prev-button');
const nextButton = document.getElementById('next-button');
const speakButton = document.getElementById('speak-button');

const wordDisplay = document.getElementById('displayed-word');
let currentWordIndex = 0;
const wordGroupSelectElement = document.getElementById('word-group');
const voiceSelectElement = document.getElementById('voice');
let currentWordGroup = Number(wordGroupSelectElement.value);

const synth = window.speechSynthesis;
let selectedVoice

const priorityVoices = ["Google US English", "Google UK English Male", "Karmit", "Samantha", "Daniel", "Tessa", "Karen", "Melina"];

function getVoices() {
  return new Promise(
    function (resolve, reject) {
      let synth = window.speechSynthesis;
      let id;
      id = setInterval(() => {
        if (synth.getVoices().length !== 0) {
          resolve(synth.getVoices());
          clearInterval(id);
        }
      }, 100);
    }
  )
}


document.addEventListener('DOMContentLoaded', () => {
  displayWord();
  getVoices().then((voices) => {
    voiceSelectElement.innerHTML = '';
    voices
      .filter(voice => priorityVoices.includes(voice.name))
      .forEach(voice => {
        const option = document.createElement('option');
        option.value = voice.name;
        option.textContent = voice.name;
        voiceSelectElement.appendChild(option);
    });
    const priorityVoiceName = priorityVoices.find(priorityVoice => voices.some(voice => voice.name === priorityVoice));
    selectedVoice = voices.find(voice => voice.name === priorityVoiceName);
    if(selectedVoice) {
      voiceSelectElement.value = selectedVoice.name;
    }
  });
});

function displayWord() {
  const currentGroup = getCurrentGroupWords()
  if(currentGroup) {
    wordDisplay.textContent = currentGroup[currentWordIndex][0]; // will display the word type (or figure of speech) later
  }
}

function getCurrentGroupWords() {
  switch (currentWordGroup) {
    case 1:
      return oneWordLetters;
    case 2:
      return twoWordLetters;
    case 3:
      return threeWordLetters;
    case 4:
      return fourWordLetters;
    case 5:
      return fiveWordLetters;
    case 6:
      return sixWordLetters;
    case 7:
      return sevenWordLetters;
    case 8:
      return eightWordLetters;
    case 9:
      return nineWordLetters;
    case 10:
      return tenWordLetters;
    case 11:
      return elevenWordLetters;
    case 12:
      return twelveWordLetters;
    case 13:
      return thirteenWordLetters;
    case 14:
      return fourteenWordLetters;
    case 15:
      return fifteenWordLetters;
    case 17:
      return seventeenWordLetters;
    default:
      return null;
  }
}

function speakWord(text) {
  if (!synth) {
    alert("Speaking not supported in your browser. Please use Chrome or Firefox.");
    return;
  }

  if (synth.speaking) {
    synth.cancel();
  }
  // console.log(synth.getVoices().map(voice => voice.name));
  const utterThis = new SpeechSynthesisUtterance(text);
  utterThis.addEventListener("error", () => {
    console.log("Error speaking. Please try again.");
  });
  utterThis.rate = 0.8; // Adjust speed (slower for kids)
  utterThis.pitch = 1; // Adjust pitch (higher for kids)
  // utterThis.lang = "en-ZA" // Use South African English pronunciation
  // utterThis.lang = "en-GB"; // Use British English pronunciation
  utterThis.lang = "en-US"; // Use American English pronunciation
  if (selectedVoice) {
    utterThis.voice = selectedVoice;
  }
  synth.speak(utterThis);
}

wordGroupSelectElement.addEventListener('change', (event) => {
  currentWordGroup = Number(event.target.value);
  currentWordIndex = 0;
  displayWord()
});

prevButton.addEventListener('click', () => {
  currentWordIndex--;
  if (currentWordIndex < 0) { // cycle
    const group = getCurrentGroupWords()?.length ?? 1
    currentWordIndex = group - 1;
  }
  displayWord();
});

nextButton.addEventListener('click', () => {
  currentWordIndex++;
  if (currentWordIndex >= (getCurrentGroupWords()?.length ?? -10)) { // cycle
    currentWordIndex = 0;
  }
  displayWord();
});

speakButton.addEventListener('click', () => {
  if (wordDisplay.innerText) {
    speakWord(wordDisplay.innerText);
  }
});

voiceSelectElement.addEventListener('change', (event) => {
  getVoices().then((voices) => {
    selectedVoice = voices.find(voice => voice.name === event.target.value);
  });
});

// key mapping
// enter->speak, left->prev, right->next
document.addEventListener('keydown', (event) => {
  switch (event.key) {
    case "ArrowLeft":
      prevButton.click();
     // speakButton.click();
      break;
    case "ArrowRight":
      nextButton.click();
    //  speakButton.click();
      break;
    case "Enter":
      speakButton.click();
      break;
    default:
      break;
  }
});

